import React, {ChangeEvent, MouseEventHandler, useState} from 'react';

type ThreeOptionSelectType ={
    items:ItemsType[]
    value:number
}

type ItemsType = {
    value:number
    title:string
}

export const ThreeOptionSelect = (props:ThreeOptionSelectType) => {

    const TitleFind = props.items.find((el)=>el.value===props.value )


    const [editMode,setEdit]=useState(false)

    const ChangeStatus=() => {
        setEdit(!editMode)
    }

    const [select,SetSelect]=useState('Minsk')

    const ChangeEnableSpan =(title:string)=> {
        SetSelect(title)
        setEdit(!editMode)
    }

    return (
        <>
            <div>
                <h3 onClick={ChangeStatus}>{select}</h3>
                {editMode &&
                    <div>
                        {props.items.map((el)=>{
                            return (
                                <div onClick={()=>ChangeEnableSpan(el.title)} >{el.title}</div>
                            )
                        })}
                    </div>

                }
            </div>


        </>
    );
};
