import {useState} from "react";

type SelectedType = {
    items:ItemType[]
}

type ItemType = {
    title:string,
    value:any
}


export const Selected = (props:SelectedType)=>{

    const [editMode, setEditMode] = useState(false)

    const ChangeEditMode = () => {
        setEditMode(!editMode)
    }
   return (
       <div onBlur={ChangeEditMode}>{props.items.map((i)=>{
           return(
               <> {editMode
                   ? <select value={i.title} onBlur={ChangeEditMode} autoFocus onChange={ChangeEnableSpan}/>
                   : <span onDoubleClick={ChangeEditMode}>{i.title}</span>}
               </>
           )
       })}</div>
   )
}
