import React, { useState, useEffect } from 'react';
import s from './clock.module.css'

export default {
    title: 'Clock ',

}

export function Clock() {
    const [hourDegrees, setHourDegrees] = useState(0);
    const [minuteDegrees, setMinuteDegrees] = useState(0);

    useEffect(() => {
        const intervalId = setInterval(() => {
            const now = new Date();
            const hours = now.getHours();
            const minutes = now.getMinutes();
            const hourDegrees = ((hours / 12) * 360) + ((minutes / 60) * 30) + 90;
            const minuteDegrees = ((minutes / 60) * 360) + 90;
            setHourDegrees(hourDegrees);
            setMinuteDegrees(minuteDegrees);
        }, 1000);
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div className={s.clock}>
            <div className={s.hourHand} style={{ transform: `rotate(${hourDegrees}deg)` }}></div>
            <div className={s.minuteHand} style={{ transform: `rotate(${minuteDegrees}deg)` }}></div>
        </div>
    );
}






