    console.log('users')
    return <div>
        <button onClick={()=>props.addCount()}>+</button>
        {props.user.map((el,index)=>{
            return(
                <li key={index}>
                    {el}
                </li>
            )
        })}
    </div>
}
const  TableSecret=React.memo(Table)
