import {ChangeEvent, useState} from "react";

type SelectedType = {
    items:ItemType[]
    changeEnableSpan: (e: string) => void
    value:string
}

type ItemType = {
    title:string,
    id:any
}


export const Selected = (props:SelectedType)=>{

    const [editMode, setEditMode] = useState(false)

    const ChangeEditMode = () => {
        setEditMode(!editMode)
    }

    const ChangeEnableSpan = (e: ChangeEvent<HTMLInputElement>) => {
        props.changeEnableSpan(e.currentTarget.value)
    }

    return (
        <> {editMode
            ? <input value={props.value} onBlur={ChangeEditMode} autoFocus onChange={ChangeEnableSpan}/>
            : <span onDoubleClick={ChangeEditMode}>{props.value}</span>}
        </>
    );
}
