import React, {ChangeEvent, useState} from 'react';

type ThreeOptionSelectType ={
    items:ItemsType[]
    value:number
}

type ItemsType = {
    value:number
    title:string
}

export const ThreeOptionSelect = (props:ThreeOptionSelectType) => {

    const TitleFind = props.items.find((el)=>el.value===props.value )


    const [editMode,setEdit]=useState(false)

    const ChangeStatus=() => {
        setEdit(!editMode)
    }

    const [select,SetSelect]=useState('Minsk')

    const ChangeEnableSpan =(e:ChangeEvent<HTMLSelectElement>)=> {
        SetSelect(e.currentTarget.value)
    }

    return (
        <> {editMode
            ? <select autoFocus   onBlur={ChangeStatus}  onChange={ChangeEnableSpan}>
                {props.items.map((el)=>{
                    return (
                    <option >  {el.title}</option>
                )
                })}
            </select>


            : <span onDoubleClick={ChangeStatus}>{select}</span>}
        </>
    );
};
