import React, {useState} from "react";

function Clock() {
    const [timerId, setTimerId] = useState<number | undefined>(undefined)

    const [date, setDate] = useState<Date>(new Date(( Date.now())))

    const [editMode,setEditMode]=useState(false)

    const start = () => {
        setTimerId(+setInterval(() => {
            setDate(new Date())
        }, 1000))
        setEditMode(true)
    };




    const stop = () => {
        // пишут студенты // поставить часы на паузу, обнулить ид таймера (timerId <- undefined)
        clearInterval(timerId);
        setEditMode(false)

    }



    let formatterTime = new Intl.DateTimeFormat("ru", {
        hour: "numeric",
        minute: "numeric",
        second: "numeric"
    });

    let formatterDate = new Intl.DateTimeFormat("ru", {
        day: "numeric",
        month: "numeric",
        year: "numeric",
    });

    let formatterWeekendDay = new Intl.DateTimeFormat("en", {
        weekday: "long",

    });

    let formatterMonth = new Intl.DateTimeFormat("en", {
        month: "long",

    });

    const stringTime = `${formatterTime.format(date)}`||<br/> // часы24:минуты:секунды (01:02:03)/(23:02:03)/(24:00:00)/(00:00:01) // пишут студенты



    return (
        <div >


                <span >
                    <strong>{stringTime}</strong>
                </span>




            <div >
                <button

                    disabled={editMode} // пишут студенты // задизэйблить если таймер запущен
                    onClick={start}
                >
                    start
                </button>
                <button

                    disabled={!editMode} // пишут студенты // задизэйблить если таймер не запущен
                    onClick={stop}
                >
                    stop
                </button>
            </div>
        </div>
    )
}


export default Clock
